UPDATE    modw_supremm.job j,    modw.job_tasks jt,    modw.job_records jr SET    j.account_id = jr.account_id,    j.fos_id = jr.fos_id,    j.person_id = jt.person_id,    j.person_organization_id = jt.person_organization_id,    j.principalinvestigator_person_id = jr.principalinvestigator_person_id,    j.piperson_organization_id = jr.piperson_organization_id,    j.systemaccount_id = jt.systemaccount_id,    j.tg_job_id = jt.job_id WHERE    j.resource_id = jt.resource_id    AND j.local_job_id = jt.local_job_id_raw    AND j.end_time_ts = jt.end_time_ts    AND j.tg_job_id != jt.job_id    AND jr.job_record_id = jt.job_record_id    AND j.tg_job_id = -1